import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-comisiones',
  templateUrl: './comisiones.component.html',
  styleUrls: ['./comisiones.component.css']
})
export class ComisionesComponent {
  fechaInicio: string = '';
  fechaFin: string = '';
  comisiones: any[] = [];

  constructor(private http: HttpClient) {}

  calcularComisiones() {
    if (!this.fechaInicio || !this.fechaFin) {
      alert('Por favor selecciona ambas fechas');
      return;
    }

    const url = `http://localhost:5189/api/Comisiones?fechaInicio=${this.fechaInicio}&fechaFin=${this.fechaFin}`;

    this.http.get<any[]>(url).subscribe({
      next: data => this.comisiones = data,
      error: err => {
        console.error(err);
        alert('Error al consultar las comisiones');
      }
    });
  }
}
<div class="container">
  <h2>Calcular comisiones por rango de fechas</h2>

  <label>Fecha Inicio:</label>
  <input type="date" [(ngModel)]="fechaInicio">

  <label>Fecha Fin:</label>
  <input type="date" [(ngModel)]="fechaFin">

  <button (click)="calcularComisiones()">Calcular</button>

  <table *ngIf="comisiones.length > 0">
    <thead>
      <tr>
        <th>Vendedor</th>
        <th>Total Ventas</th>
        <th>Comisión</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of comisiones">
        <td>{{ item.vendedor }}</td>
        <td>{{ item.totalVentas }}</td>
        <td>{{ item.comision }}</td>
      </tr>
    </tbody>
  </table>
</div>
